import React, { Component } from 'react';
import './Home.css';
import { parse } from 'query-string';
import {BrowserRouter as Router, Route, Link} from 'react-router-dom'; 
import ReactDOM from 'react-dom'; 
class Home extends Component {
    constructor(props){
        super(props);
        this.state ={
            items: [],
            isLoaded: false,

        }
    }
    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/users')
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded: true,
                items: json,
            })
        });
    } 
    render(){
        var { isLoaded, items} = this.state;
        if(!isLoaded){
            return <div><img width="300" height="200" src="/Images/loader.gif"/></div>
        }
        else{
            return(
                <div className="mainDiv">
                    <div className="mainBanner">
                        <div className="imageMain">
                        <Link to={`/Category/?all`}><img src="https://www.circuitcity.com/pub/media/hero/cc_1350x525_c.jpg" /></Link>
                        </div>
                    </div>
                    <div className="featuredProducts">
                        <h2>Featured Products</h2>
                        <div className="imagesHolder">
                            {items.map(item => (
                                <div className="product" key={item.id}>
                                        <Link to={`/Product/?${item.id}`}>
                                    <div className="email">Email:{item.email}</div>
                                    <div className="name"><img src={`/Images/${item.id}.jpg`} /></div>
                                    <div className="person">{item.name}</div>
                                    <div className="phone">Ph:{item.phone}</div>
                                    <div className="addToCart">
                                        <a href="">Add to Cart</a>
                                    </div>
                                    </Link>
                                </div>
                            ))}
                            
                        </div>
                    </div>
                </div>
            )
        }
}
}
export default Home;
