import React, { Component } from 'react';
import {BrowserRouter as Router, Route, Link} from 'react-router-dom';

class LeftMenu extends Component{
    constructor(props){
        super(props);
        this.state={
            items: [],
            isLoaded: false,
        };
    }
    componentDidMount(){
        fetch('http://192.168.7.167/wcs/resources/store/11901/categoryview/@top')
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded: true,
                items: json,
            })
        });
    }

    render(){
        var {isLoaded, items}= this.state;
        if(!isLoaded){
            return <div>Loading...</div>
        }
        else{
            return(
                <div className="menuHolder">
                        
                    {[items].map(item => (
                        <div className="cateItem" key={item.id}>
                            <Link to={`/SubCategory/?${item.id}`}>{item.address.city}</Link>
                        </div>
                    ))}
                    <div className="cateItem" >
                        <Link to={`/Category/?all`}>All-Categories</Link>
                    </div>
                </div>
            )
        }
    }

}
export default LeftMenu;
