import React, {Component} from 'react';  
import LeftMenu from './component/LeftMenu';
import {Link} from 'react-router-dom';
class Header extends Component{
    constructor(props){
        super(props);
        this.state = {search : "Search entire store here...",  showCart : false, cssShowHide: 'none'};
       }
       getSearch = (event) =>{
           this.setState ({search : event.target.value});
       }
       showHideCart = (event) => {
            
            if(this.hideAtStart.display == 'block'){
               // this.hideAtStart.display =  'none';
               alert('blocked');
            }
            else{
                //this.hideAtStart.display =  'block';
                alert('none');
            }
            
            console.log(this.hideAtStart);
       }
      
    render(){
        const hideAtStart = {
            display: "none",
        };
    return(
        <div className="fullHeaderPart">
        <div className="topHeader">
            <div className="freeShip">
                <p>FREE SHIPPING over $35*</p>
            </div>
            <div className="phNmber">
                <div className="helpHolder">
                    <Link to="/contact" >Help</Link> | 1-877-234-8013 
                </div>
            </div>
            <div className="signIn">
                <Link to="/signin">Sign In</Link> or  
                <Link to="/NewAccount"> Create an Account</Link>
            </div>
        </div>
        <div className="searchBar">
            <div className="logo"> <Link to="/" ><img alt="Circuit City" src="https://www.circuitcity.com/pub/media/logo/default/logo_1.png" /></Link></div>
            <div className="searchInput">
                <input type="text" placeholder={this.state.search} onChange={this.getSearch} />
                <a href="">Search</a>
            </div>
            <div className="cartHolder">
                <a href="#" onClick={this.showHideCart}>Cart</a>
                <div  style={hideAtStart} id="cartDetailPage" name="cartDetailPage">
                    <div className="cartDetail">
                        <div>
                            <span><span className="itemInCart"></span>in Cart</span>
                        </div>
                        <div>
                            <span>Cart Subtotal: <span className="cartSubTotal"></span></span>
                        </div>
                    </div>
                    <div className="checkoutAndPaypal">
                        <div>
                            <a href="">Proceed to Checkout</a>
                        </div>
                        <div>
                            <a href="#">PayPal</a>
                        </div>
                    </div>
                    <div className="eachProduct" >
                        <div className="thumbnail"> </div>
                        <div className="otherInfo">
                            <div className="clickText"> </div>
                            <div className="price"> </div>
                            <div className="qtyEditDelete">
                                <div>
                                    <span>Qty:</span>
                                    <input type="text" placeholder="1"/>
                                </div>
                                <div><a href="#">Edit</a></div>
                                <div><a href="#">Delete</a></div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="shipCart"></div>
        </div>
        <div className="mainMenu">
           <LeftMenu />
        </div>
        </div>
    )
    }
}
export default Header;
