import React, { Component } from 'react';
import './SignIn.css'
class SignIn extends Component{
    constructor(props){
        super(props);
        this.state = {userName : '', Password: '', formSubmit: false};
    }
    getChangeHandler = (event) =>{
        this.setState({userName: event.target.value});
    } 
    myHandler = (event) =>{
        this.setState({userName: event.target.value});
    }
    getSubmitForm = (event) =>{
        this.setState({formSubmit: true });
    }
    render(){
        if(!this.state.formSubmit){
            
        return(
            <div className="signInPage">
                <h1>Sign In</h1>
                <div><input type="text" id="yName" placeholder="your name" onChange={this.myHandler} /></div>
                <div><input type="text"  id="yEmail" placeholder="karim@karim.com" /></div>
                <div><input type="button" onClick={this.getSubmitForm} value="Sign In"/></div>
            </div>
        )
        }
        else{
            //{this.setState.formSubmit= false;}
            return(
                <div className="signInPage">
                <br/>
                <br/>
                    <br/>
                    <br/>
                <h2>Welcome: <span>{this.state.userName}</span></h2> 
                </div>
            )
        }
    }
}export default SignIn;
