//import 'aframe';
import { Scene, Entity } from 'aframe-react';
import React from 'react';


 
class VREx extends React.Component {

  state = {
    missile: false,
    launching: false
}

launch = () => {
    this.setState({
        missile: true,
        launching: true,
        missilePos: { x: 0, y: -4, z: 0 },
        missileA: { ax: 0, ay: .005, az: 0 },
        missileV: { vx: 0, vy: 0, vz: 0 }
    });
    if (!this.launched) {
        window.requestAnimationFrame(() => this.gameLoop());
        this.launched = true;
    }
}

gameLoop() {
    const { x, y, z } = this.state.missilePos,
          { vx, vy, vz } = this.state.missileV,
          { ax, ay, az } = this.state.missileA;

    console.log(this.state.missilePos);

    this.setState({
        missilePos: { x: x+vx, y: y+vy, z: z+vz },
        missileV: { vx: vx+ax, vy: vy+ay, vz: vz+az }
    });
    window.requestAnimationFrame(() => this.gameLoop());
}


  render () {
    return (
      <div className="VREx">
  { //  
      // <a-scene embedded arjs='trackingMethod: best; sourceType: webcam; debugUIEnabled: true;'>

  }
   <a-scene artoolkit={{sourceType: 'webcam', trackingMethod: 'best'}}>
      <a-entity gltf-model="Images/90a30469-f038-4054-be9c-fd1ec94a810d_kitchentest.glb" scale="60 60 -77" rotation="1.00 1.00 -5.00" position="2 3 -10">
     </a-entity>   
     
     <ntity camera  />      
  </a-scene>
  </div>
    );
  }
}


export default VREx;
