import React, {Component}  from 'react';
import { parse } from 'query-string';
import {BrowserRouter as Router, Route, Link} from 'react-router-dom'; 
import ReactDOM from 'react-dom'; 
import LeftMenu from './LeftMenu';
import queryString from 'querystring'
import './category.css';

class SubCategory extends Component{
    constructor(props){
        super(props);
        this.state ={
            items: [],
            isLoaded: false,
            categoryCalled: '',
            callSubCate : ''
        }
    
    }
    
    // componentDidUpdate(props){
    //     fetch('http://192.168.7.167/wcs/resources/store/11901/categoryview/byId/'+this.props.location.search.substring(1.1))
    //     .then(res => res.json())
    //     .then(json => {
    //         this.setState({
    //             isLoaded: true,
    //             items: json,
    //             categoryCalled: this.props.location.search.substring(1.1)
    //         })
    //     });
    //     console.log(props);
    // }
    
    componentWillReceiveProps(props, state){ 
        // if(this.props.location.search != this.state.categoryCalled){
            this.getDataFromServer(this.props.callCat);
         //}
         
    }
    componentDidMount(props){
        console.log('compnnent did mount rn');
        this.getDataFromServer(props);  
    } 
    getDataFromServer = (getCat) => {
        let params = '11613'
        console.log('http://192.168.7.167/wcs/resources/store/11901/categoryview/byId/'+getCat);
        //const { handle } = this.props.location.search; 
        fetch('http://192.168.7.167/wcs/resources/store/11901/categoryview/byId/'+params)//+this.props.location.search.substring(1.1))
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded: true,
                items: json,
                //categoryCalled: this.props.location.search,
                //callSubCate : this.props.location.search.substring(1.1)
            })
        });
        
        
    }
    render(){
         
        var { isLoaded, items} = this.state;
        if(!isLoaded){
            return <div><img width="300" height="200" src="/Images/loader.gif"/></div>
        }
        else{
           // console.log(this.props.location.search.cat+"-----")calledItem = {this.state.callSubCate}
            return(
                <div>
                    {[items].map(item => (
                        <div  key={item.recordSetTotal}> 
                        {item.CatalogGroupView.map(insideItems => (
                            <div className="product" key={insideItems.uniqueID}>
                                <div className="name">
                                <Link to={`/Product/?${insideItems.uniqueID}`}>
                                    <div className="name"><img src={`/Images/${insideItems.uniqueID}.jpg`} /></div>
                                </Link>
                                </div>
                                <div className="person">{insideItems.name}</div> 
                                <div className="addToCart">
                                <Link to={`/Product/?${insideItems.uniqueID}`}>
                                    Add to Cart
                                </Link>
                                </div>

                            </div>
                        ))} 
                            
                    </div>
                    ))}
                </div>             
            )
        }
}
}
 
export default SubCategory;
