import React, { Component } from 'react';
import './SignIn.css'
import {BrowserRouter as Router, Route, Link} from 'react-router-dom';
class SignIn extends Component{
    constructor(props){
        super(props);
        this.state = {userName : '', isAuthenticated: false, Token: '', errorMsg: ''};
    }
    getChangeHandler = (event) =>{
        this.setState({userName: event.target.value});
    } 
    myHandler = (event) =>{
        this.setState({userName: event.target.value});
    }
    getSubmitForm = (event) =>{
        this.setState({formSubmit: true });
    }
    componentDidMount(){
        const payloads = {
            logonId: "zarak786@gmail.com", //zarak786@gmail.com karim.zarak@royalcyber.com
            logonPassword: "Revert!23"
        }
        fetch('https://192.168.7.167/wcs/resources/store/11901/loginidentity?responseFormat=json',{
            method: 'POST',
            headers: {
                'accept': 'application/json',
                'Content-type': 'application/json'
            },
            body: JSON.stringify(payloads)
        })
        .then(res=>res.json())
        .then((data)=>{
            // console.log(data.contains(data.errors[0].errorParameters));
            // if(data.errors[0] === undefined ){
            //     if(data.errorCode === '2110'){  
            //     this.setState({
            //         errorMsg : 'Due to '+data.errors[0].errorParameters+' unsuccessful password attempts, you will be unable to sign in. Contact a store representative to enable your user account.'
            //     });
            //     }
            // }
            // else{
                
                this.setState({
                    isAuthenticated: true,
                    Token: data.WCToken,
                    userName: payloads.logonId
                });
            //}
            if(data.errors != 'undefined'){
                console.log(data.errors.length +"<error 0>" );
                console.log(data);
            }
            else if( data.WCToken.length += 'undefined'){
                console.log("<wcToken>"+ data.WCToken.length);
            }
            else{
                console.log('Else Statement');
            }
        },
        (error) => {
            
            this.setState({
                isAuthenticated: false
            });
            console.log(this.state.isAuthenticated+"Error Data>>"+error);
        }); 
    }
    
    render(){
        if(!this.state.isAuthenticated){
            
        return(
            <div className="signInPage">
                <br/> 
                <h1>Customer Login </h1>
                <br/> 
               <div className="costomerRegistration">
                    <h2>Registered Customers</h2>
                    <br/>
                    <div className="lableInput">
                        <div className="lable">
                            <span>Email  </span><span className="starik">*</span>
                        </div>
                        <div className="getInput"><input type="text" id="yName" onChange={this.myHandler} /></div>
                    </div>
                    <br/><br/><br/>
                    <div className="lableInput">
                        <div className="lable">
                            <span>Password </span><span className="starik">*</span>
                        </div>
                        <div className="getInput"><input type="password"  id="yEmail" /></div>
                    </div>
                    <br/><br/><br/>
                   
                    <div className="nextBtn"><a href="#" onClick={this.getSubmitForm} >Sign In</a></div>
                    <br/><br/>   
                    <p className="errorMessage">
                        {this.state.errorMsg}
                    </p>
                    <br/><br/><br/>
               </div>
               <div className="newCostomer">
               <h2>New Customers</h2>
               <br/>
                    <div><p className="signInPara">Creating an account has many benefits: check out faster, keep more than one address, track orders and more.</p> </div> 
                    <br/> 
                    <div className="nextBtn">
                        <Link to="/NewAccount" className="nextBtn">Create an Account</Link></div>
               </div>
               <br/><br/><br/>
            </div>
        )
        }
        else{
            //{this.setState.formSubmit= false;}
            return(
                <div className="signInPage afterSignIn"> 
                <br/>
                <br/>
                    <br/>
                    <br/>
                <h2  >{this.state.userName} Successfully Logged In  <br/> Your Token is : </h2> 
                <p><span>{this.state.Token}</span></p>
                </div>
            )
        }
    }
}export default SignIn;
