import React, {Component}  from 'react'; 
import { Link} from 'react-router-dom';  
import LeftMenu from './LeftMenu';
import './category.css';

class Category extends Component{
    constructor(props){
        super(props);
        this.state ={
            items: [],
            isLoaded: false,
        }
    }
    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/users')
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded: true,
                items: json,
            })
        });
    } 
    render(){
        const categories = [
            {
                id:1,
                CateId:1,
                ProductName:"Kalorik JK 44499 SS - Kettle - 1.1 qt - 1000 W",
                Description:"You're reviewing: Kalorik JK 44499 SS - Kettle - 1.1 qt - 1000 W",
                Image:"https://www.circuitcity.com/pub/media/catalog/product/cache/6a1431ae00d1e851b3c9d53c89ac064b/k/a/kal-jk44499ss_jfjgordvohjqrikg.jpg",
                ImageId:"kal-jk44499ss_jfjgordvohjqrikg",
                Price:2500
            },
            {
                id:2,
                CateId:1,
                ProductName:"BLACK+DECKER MX3000W ",
                Description:"BLACK+DECKER MX3000W - Hand mixer - 250 W - white/gray",
                Image:"https://www.circuitcity.com/pub/media/catalog/product/cache/6a1431ae00d1e851b3c9d53c89ac064b/h/o/hon-mncover848987001005_rhmvzgo6gqeszxmq.jpg",
                ImageId:"hon-mncover848987001005_rhmvzgo6gqeszxmq",
                Price:5500
            }
        ]
        var { isLoaded, items} = this.state;
        if(!isLoaded){
            return <div><img width="300" height="200" src="/Images/loader.gif"/></div>
        }
        else{
            return(
                <div className="mainDiv CategoryPage"> 
                    <div className="featuredProducts"> 
                        <div className="leftMenu"><p>Main Menu</p><LeftMenu /></div>
                        <div className="imagesHolder">
                            {categories.map(item => (
                                <div className="product" key={item.id}>
                                    
                                    <div className="email">Email:{item.email}</div>
                                    <Link to={`/Product/?${item.id}`}>
                                        <div className="name"><img src={`/Images/${item.id}.jpg`} /></div>
                                    </Link>
                                    <div className="person">{item.name}</div>
                                    <div className="phone">Ph:{item.phone}</div>
                                    <div className="addToCart">
                                    <Link to={`/Product/?${item.id}`}>
                                        Add to Cart
                                    </Link>
                                    </div>
                                </div>
                            ))}
                            
                        </div>
                    </div>
                </div>
            )
        }
}
}
 
export default Category;
