import React, {Component}  from 'react'; 
import { Link} from 'react-router-dom';  
import LeftMenu from './LeftMenu';
import SubCategory from './SubCategory'
import './category.css';

class Category extends Component{
    constructor(props){
        super(props);
        this.state ={
            items: [],
            isLoaded: false,
        }
    }
    componentDidMount(){
        fetch('http://192.168.7.167/wcs/resources/store/11901/categoryview/@top')
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded: true,
                items: json,
            })
        });
    } 
    render(){
        return(
        <div className="mainDiv CategoryPage"> 
            <div className="featuredProducts"> 
                <div className="leftMenu"><p>Main Menu</p><LeftMenu /></div>
                <div className="imagesHolder"><SubCategory callCat = {13103} /></div>
            </div>
        </div>
        )
    }
}
 
export default Category;
