const iState ={
    userName: 'Karim',
    email:  'karim@karim.com',
    WCToken: ''
}

const userReducer = (state = iState, action) =>{
    console.log(action);
    if(action.type === 'LOGED_USER'){
        return{
            ...state,
            userName: action.payloads.user,
            WCToken: action.payloads.tokn
        }
    }
    // else if(action.type === 'LOGED_TOKEN'){
    //     return{
    //         ...state,
    //         WCToken: action.payloads
    //     }
    // }
    return state;
}
export default userReducer;
