import React, { Component } from 'react';
import './SignIn.css'
class SignIn extends Component{
    constructor(props){
        super(props);
        this.state = {userName : '', Password: '', formSubmit: false};
    }
    getChangeHandler = (event) =>{
        this.setState({userName: event.target.value});
    } 
    myHandler = (event) =>{
        this.setState({userName: event.target.value});
    }
    getSubmitForm = (event) =>{
        this.setState({formSubmit: true });
    }
    render(){
        if(!this.state.formSubmit){
            
        return(
            <div className="signInPage">
                
                <h1>Customer Login</h1>
               <div className="costomerRegistration">
                    <h2>Registered Customers</h2>
                    <br/>
                    <div className="lableInput">
                        <div className="lable">
                            <span>Email  </span><span className="starik">*</span>
                        </div>
                        <div class="getInput"><input type="text" id="yName" onChange={this.myHandler} /></div>
                    </div>
                    <br/><br/><br/>
                    <div className="lableInput">
                        <div className="lable">
                            <span>Password </span><span className="starik">*</span>
                        </div>
                        <div class="getInput"><input type="password"  id="yEmail" /></div>
                    </div>
                    <br/><br/><br/>
                   
                    <div className="nextBtn"><a href="#" onClick={this.getSubmitForm} >Sign In</a></div>
                    <br/><br/><br/>
               </div>
               <div className="newCostomer">
               <h2>New Customers</h2>
               <br/>
                    <div><p className="signInPara">Creating an account has many benefits: check out faster, keep more than one address, track orders and more.</p> </div> 
                    <div className="nextBtn"><a href="#" className="nextBtn">Create an Account</a></div>
               </div>
               <br/><br/><br/>
            </div>
        )
        }
        else{
            //{this.setState.formSubmit= false;}
            return(
                <div className="signInPage">
                <br/>
                <br/>
                    <br/>
                    <br/>
                <h2>Welcome: <span>{this.state.userName}</span></h2> 
                </div>
            )
        }
    }
}export default SignIn;
