import React, { Component } from 'react';
import './NewAccount.css';
class NewAccount extends Component{
    constructor(props){
        super(props);
        this.state = {userName : '', emailId: '', Password : '', passwordConfirm: ''};
    }
    handleEachItem = (event) => {
        //this.setState ({userName = this.state.});
        //this.setState({ userName : event.target.value});
        console.log(event.target.value);
      this.setState({ [event.target.name] : event.target.value});  
    }
    submitForm = (e) => {
        e.preventDefault();
        alert("welcome"+JSON.stringify(this.state));
    }
    render(){
        return(
            <div className="checkoutPage">
                <h3>Create New Customer Account</h3>
                <div className="clearBoth"></div>
                <div className="items">
                    <div className="lableInput">
                        <div className="lable">
                            <h2>Personal Information</h2>
                        </div>
                        <div className="getInput">
                           
                        </div>
                    </div>
                    <div className="lableInput">
                        <div className="lable">
                            <span>First Name </span><span className="starik">*</span>
                        </div>
                        <div className="getInput">
                            <input type="text" />
                        </div>
                    </div>
                    
                    <div className="lableInput">
                        <div className="lable">
                            <span>Last Name </span><span className="starik">*</span>
                        </div>
                        <div className="getInput">
                            <input type="text" />
                        </div>
                    </div>
                    <div className="lableInput">
                        <div className="lable">
                            
                        </div>
                        <div className="getInput">
                            <input type="checkbox" />Sign Up for Newsletter
                        </div>
                    </div>
                    <div className="lableInput">
                        <div className="lable">
                            <span>Email </span><span className="starik">*</span>
                        </div>
                        <div className="getInput">
                            <input type="text" />
                        </div>
                    </div>
                    <div className="lableInput">
                        <div className="lable">
                            <span>Order Number  </span><span className="starik">*</span>
                        </div>
                        <div className="getInput">
                             <input type="text" />
                        </div>
                    </div>
                    <div className="lableInput">
                        <div className="lable">
                            <span>Message </span><span className="starik">*</span>
                        </div>
                        <div className="getInput">
                            <textarea cols="97"  rows="5"> </textarea>
                        </div>
                    </div>
                    
                    <div className="nextBtn"><a href="#">Submit </a></div>
                    <br/><br/><br/>
                    
                </div>
                
            </div>
        );
    }
}
export default NewAccount;
