import React, {Component}  from 'react'; 
import { Link} from 'react-router-dom';  
import LeftMenu from './LeftMenu';
import './category.css';

class Category extends Component{
    constructor(props){
        super(props);
        this.state ={
            items: [],
            isLoaded: false,
        }
    }
    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/users')
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded: true,
                items: json,
            })
        });
    } 
    render(){
        var { isLoaded, items} = this.state;
        if(!isLoaded){
            return <div><img width="300" height="200" src="/Images/loader.gif"/></div>
        }
        else{
            return(
                <div className="mainDiv CategoryPage"> 
                    <div className="featuredProducts"> 
                        <div className="leftMenu"><p>Main Menu</p><LeftMenu /></div>
                        <div className="imagesHolder">
                            {items.map(item => (
                                <div className="product" key={item.id}>
                                    <Link to={`/Product/?${item.id}`}>
                                    <div className="email">Email:{item.email}</div>
                                    <div className="name"><img src={`/Images/${item.id}.jpg`} /></div>
                                    <div className="person">{item.name}</div>
                                    <div className="phone">Ph:{item.phone}</div>
                                    </Link>
                                </div>
                            ))}
                            
                        </div>
                    </div>
                </div>
            )
        }
}
}
 
export default Category;
