import React, {Component} from 'react';  
import LeftMenu from './component/LeftMenu';
import {Link} from 'react-router-dom';
class Header extends Component{
    constructor(props){
        super(props);
        this.state = {search : "Search entire store here...",  showCart : false, cssShowHide: 'none'};
       }
       getSearch = (event) =>{
           this.setState ({search : event.target.value});
       }
       showHideCart = (event) => {
            alert();
       }
      
    render(){
        const hideAtStart = {
            display: "none"
        }
        console.log(hideAtStart);
    return(
        <div className="fullHeaderPart">
        <div className="topHeader">
            <div className="freeShip">
                <p>FREE SHIPPING over $35*</p>
            </div>
            <div className="phNmber">
                <div className="helpHolder">
                    <Link to="/contact" >Help</Link> | 1-877-234-8013 
                </div>
            </div>
            <div className="signIn">
                <Link to="/signin">Sign In</Link> or  
                <Link to="/NewAccount"> Create an Account</Link>
            </div>
        </div>
        <div className="searchBar">
            <div className="logo"> <Link to="/" ><img alt="Circuit City" src="https://www.circuitcity.com/pub/media/logo/default/logo_1.png" /></Link></div>
            <div className="searchInput">
                <input type="text" placeholder={this.state.search} onChange={this.getSearch} />
                <a href="">Search</a>
            </div>
            <div className="cartHolder">
                <a href="#" onClick={this.showHideCart}>Cart</a>
                
            </div>
            <div className="shipCart"></div>
        </div>
        <div className="mainMenu">
           <LeftMenu />
        </div>
        </div>
    )
    }
}
export default Header;
