import React, { Component } from 'react';
import {BrowserRouter as Router, Route, Link} from 'react-router-dom';
import queryString from 'querystring'

class LeftMenu extends Component{
    constructor(props){
        super(props);
        this.state={
            items: [],
            isLoaded: false,
            categoryCalled: ''
        };
        console.log(props)
    }
    componentDidMount(props){
        fetch('http://192.168.7.167/wcs/resources/store/11901/categoryview/byParentCategory/15601')//+this.props.location.search.substring(1.1))
        .then(res => res.json())
        .then(json => {
            this.setState({
                isLoaded: true,
                items: json, 
            })
        });
        console.log( '<<<<<component did mount >>>>' )
    }
    componentWillReceiveProps(props){
        // let getParams = queryString.parse(this.props.search)
        // console.log(getParams.cat+"----90-")
        //console.log(this.props.location.search+'<<<<<<<<<<<------------')
        
            //if(getParams.cat != undefined || getParams.cat == 'Static'){
                fetch('http://192.168.7.167/wcs/resources/store/11901/categoryview/byParentCategory/13101')//+this.props.calledItem)
                .then(res => res.json())
                .then(json => {
                    this.setState({
                        isLoaded: true,
                        items: json,
                    })
                });
                this.setState({categoryCalled: '13103'})
            //}
          
             
       
    }
    render(){
        var {isLoaded, items}= this.state;
        if(!isLoaded){
            return <div>Loading...</div>
        }
        else{
            console.log(this.state.categoryCalled)
            return(
                <div className="menuHolder">
                        
                    {[items].map((item, index) => (
                        <div key={item.recordSetTotal}>
                            {item.CatalogGroupView.map(insideItems => (
                        <div className="cateItem" key={insideItems.uniqueID}>
                            <Link to={`/Category/?${insideItems.uniqueID}&cat=Static`}>{insideItems.name}</Link>
                        </div>
                            ))}
                        </div>
                    ))}
                    <div className="cateItem" >
                        <Link to={`/Category/?all`}>All-Categories</Link>
                    </div>
                </div>
            )
        }
    }

}
export default LeftMenu;
