import React, { Component } from 'react';
import './NewAccount.css';
class NewAccount extends Component{
    constructor(props){
        super(props);
        this.state = {userName : '', emailId: '', Password : '', passwordConfirm: ''};
    }
    handleEachItem = (event) => {
        //this.setState ({userName = this.state.});
        //this.setState({ userName : event.target.value});
        console.log(event.target.value);
      this.setState({ [event.target.name] : event.target.value});  
    }
    submitForm = (e) => {
        e.preventDefault();
        alert("welcome"+JSON.stringify(this.state));
    }
    render(){
        return(
            <div className="signUpPage">
                <form onSubmit = {this.submitForm}>
                    <h1>Create New Account</h1>
                    <div><input type="text" id="yName" name="userName" value={this.state.userName} onChange={this.handleEachItem} placeholder="your name" /></div>
                    <div><input type="email" id="yEmail" name="emailId" value={this.state.emailId} onChange={this.handleEachItem} placeholder="email@domain.com" /></div>
                    <div><input type="password" id="password" name="password" value={this.state.Password} onChange={this.handleEachItem} placeholder="*********" /></div>
                    <div><input type="password" id="passwordConfirm" name="passwordConfirm" value={this.state.passwordConfirm} onChange={this.handleEachItem} placeholder="*********" /></div>
                    <div><input type="submit" value="Sign Up"/></div>
                </form>
            </div>
        );
    }
}
export default NewAccount;
